SELECT NO,NAME,PRIOR_NO priorNo,REGDATE
FROM categories
WHERE no=#{no}

SELECT no,name,prior_no priorNo, regdate
FROM categories
WHERE prior_no = #{priorNo}

SELECT no,name,prior_no priorNo, regdate
FROM categories
WHERE no = #{priorNo}

