mapper.select:
SELECT NO,PRODUCT_NO productNo,DAY	
FROM delivery_days
WHERE no=#{no}

SELECT NO,PRODUCT_NO productNo,DAY	
FROM delivery_days
WHERE product_no = #{productNo}

SELECT d.day
FROM delivery_days d, products p
WHERE p.no=d.product_no AND p.no=#{no}

SELECT DAY	
FROM delivery_days
WHERE PRODUCT_NO=#{productNo}

SELECT no, product_no, day
FROM delivery_days
WHERE product_no = #{productNo}

select no,day
from delivery_days
where product_no=#{productNo}
