mapper.select:
SELECT no,id,password,type,profile_img profileImg,regdate	
FROM members
WHERE id=#{id} AND password =#{password} AND type=#{type}

mapper.update:
update members
set password = #{password}
where id = #{id}

update members
set profile_img = #{profileImg}
where no = #{no}

mapper.select:
SELECT COUNT(*)
FROM members
WHERE id=#{id}

SELECT COUNT(*)
FROM takers
WHERE nickname=#{nickname}

SELECT COUNT(*)
FROM givers
WHERE company=#{businessName}

SELECT COUNT(*)
FROM members
WHERE id=#{id}
